sections.starterCode:
class Person:
    def __init__(self, name):
        self.name = name

    def greet(self):
        print(f'Hello, I am {self.name}')

p = Person('Alice')
p.greet()

sections.solutionCode:
class Dog:
    def __init__(self, name):
        self.name = name
    def bark(self):
        print('Woof!')
d = Dog('Buddy')
d.bark()
